mapper.select:
select ch.id id, ch.real_host_id, ch.host_name, ch.account, ch.type, ch.cpu_core, ch.memory, ch.sys_disk, ch.data_disk, ch.bandwidth, ch.is_auto_startup, ch.running_status, ch.status, ch.inner_ip, ch.inner_port, ch.outer_ip, ch.outer_port, ch.region from cloud_host ch where ch.type = 4 and ch.status != 4 and ch.id in ( select w_detail.host_id from cloud_host_warehouse_detail w_detail
and w_detail.warehouse_id in
#{item}
and w_detail.status != #{warehouseDetailStatus}
)

select ch.id id, ch.real_host_id, ch.host_name, ch.display_name, ch.type, ch.cpu_core, ch.memory, ch.sys_disk, ch.data_disk, ch.bandwidth, ch.is_auto_startup, ch.running_status, ch.status status, ch.inner_ip, ch.inner_port, ch.outer_ip, ch.outer_port, ch.password, ch.region region, config.process_status, sd.name sysImageName, ch.sys_image_name, ch.inactivate_time inactivateTime, ch.create_time createTime from cloud_host ch left join cloud_host_shopping_config config on ch.id=config.host_id left join sys_disk_image sd on ch.sys_image_id = sd.id where ch.user_id = #{userId} and ch.status!=4
and ch.region = #{region}
order by ch.create_time desc

select count(1) from cloud_host ch left join sys_user s on ch.user_id = s.id left join terminal_user u on ch.user_id = u.id left join mark_bind_user mbu on u.id = mbu.user_id left join mark m on mbu.mark_id = m.id left join (select a.id, a.belonging_id,b.account from terminal_user a left join sys_user b on a.id = b.id) t on t.id = ch.user_id
1=1
and (ch.host_name like #{hostName} or ch.display_name like #{hostName})
and ch.running_status = #{queryStatus}
and ch.status=1 and ch.running_status = 1 and ch.real_host_id is not null
and ch.status=1 and ch.running_status = 2
and ch.status=2
and ch.status=3
and (ch.status=2 and #{time} > ch.inactivate_time)
and (ch.real_host_id is null or exists (select 1 from (select a.host_id from cloud_host_shopping_config a where a.process_status in ('0','3') ) b where b.host_id = ch.id))
and ch.region = #{region}
and m.id = #{markId}
and u.name like #{username}
and ch.user_id = #{userId}
and t.account like #{belongaccount} or u.phone like #{belongaccount}
and (ch.outer_ip like #{outerIp} or ch.inner_ip like #{outerIp})
and s.type = 4 and ch.status!=4

select count(1)
from cloud_host ch
join terminal_user t
	on ch.user_id = t.id

	t.belonging_id = #{agentId}
	
		and ch.host_name like #{hostName}
	
	
		and u.name = #{username}
	
	
		and t.account = #{belongaccount}
		
	and ch.status!=4

select ch.id id, ch.real_host_id, s.account user_account, ch.host_name, ch.display_name, ch.region, ch.cpu_core, ch.memory, ch.sys_disk, ch.data_disk, ch.bandwidth, ch.running_status, ch.status status, ch.is_auto_startup, ch.inner_ip, ch.inner_port, ch.outer_port, ch.outer_ip, ch.description, ch.user_id, ch.monthly_price, ch.inactivate_time, ch.sys_image_name, u.name username, m.name markname , t.account belong_account, u.phone, u.email, u.account_balance, ch.create_time createTime from cloud_host ch left join cloud_host_shopping_config config on ch.id=config.host_id left join sys_user s on ch.user_id = s.id left join terminal_user u on ch.user_id = u.id left join mark_bind_user mbu on u.id = mbu.user_id left join mark m on mbu.mark_id = m.id left join (select a.id, a.belonging_id,b.account from terminal_user a left join sys_user b on a.id = b.id) t on t.id = ch.user_id
1=1
and (ch.host_name like #{hostName} or ch.display_name like #{hostName})
and ch.running_status = #{queryStatus}
and ch.status=1 and ch.running_status = 1 and ch.real_host_id is not null
and ch.status=1 and ch.running_status = 2
and ch.status=2
and ch.status=3
and (ch.status=2 and #{time} > ch.inactivate_time)
and (ch.real_host_id is null or exists (select 1 from (select a.host_id from cloud_host_shopping_config a where a.process_status in ('0','3') ) b where b.host_id = ch.id))
and ch.region = #{region}
and m.id = #{markId}
and u.name like #{username}
and ch.user_id = #{userId}
and t.account like #{belongaccount} or u.phone like #{belongaccount}
and (ch.outer_ip like #{outerIp} or ch.inner_ip like #{outerIp})
and s.type = 4 and ch.status!=4
order by ch.create_time desc limit #{start_row}, #{row_count}

select ch.id id, u.account user_account, ch.host_name, ch.cpu_core, ch.memory, ch.sys_disk, ch.data_disk, ch.bandwidth, ch.running_status, ch.status, ch.is_auto_startup, ch.inner_ip, ch.outer_ip from cloud_host ch join sys_user u on ch.user_id = u.id join terminal_user t on t.id = u.id
t.belonging_id = #{agentId}
and ch.host_name like #{hostName}
and ch.status!=4
order by ch.host_name limit #{start_row}, #{row_count}

select ch.id id, ch.real_host_id, ch.user_id user_id, ch.host_name, ch.cpu_core, ch.memory, ch.sys_disk, ch.data_disk, ch.bandwidth, ch.running_status, ch.status, ch.is_auto_startup, ch.inner_ip, ch.outer_ip, ch.region from cloud_host ch where ch.user_id in
#{item}
and ch.status!=4

select ch.id id, ch.real_host_id, ch.host_name, ch.display_name, ch.cpu_core, ch.memory, ch.sys_disk, ch.data_disk, ch.bandwidth, ch.running_status, ch.status status, ch.is_auto_startup, ch.inner_ip, ch.outer_ip, ch.inner_port, ch.outer_port, ch.password from cloud_terminal_binding ctb join cloud_host ch on ctb.host_id = ch.id
ctb.user_id = #{userId} and ch.status!=4
and ctb.cloud_terminal_id = #{cloudTerminalId}

select ch.id , ch.real_host_id , ch.type , ch.user_id , ch.host_name , ch.account , ch.password , ch.cpu_core , ch.memory , ch.sys_image_id , ch.sys_disk , ch.data_disk , ch.bandwidth , ch.is_auto_startup, ch.running_status , ch.status , ch.inner_ip , ch.inner_port , ch.outer_ip , ch.outer_port from cloud_host ch where ch.type = 1
and ch.host_name like #{cloudHostName}
and ch.status!=4

mapper.update:
update cloud_host

	package_id = NULL

where id = #{id}

update cloud_host

	status = #{status},		
	inactivate_time = #{inactivateTime}

where user_id = #{userId}
and status!=4

update cloud_host
status = #{status}, inactivate_time = #{inactivateTime}
where user_id = #{userId} and type = #{type} and status !=2 and status!=4

update cloud_host
type = #{type}, user_id = #{userId}, host_name = #{hostName}, display_name = #{displayName}, account = #{account}, password = #{password}, cpu_core = #{cpuCore}, memory = #{memory}, bandwidth = #{bandwidth}, data_disk = #{dataDisk}, is_auto_startup = #{isAutoStartup}, monthly_price = #{monthlyPrice}, package_id = #{package_id}
where id = #{id}

update cloud_host

	cpu_core = #{cpuCore},
	memory = #{memory},
	bandwidth = #{bandwidth},
	monthly_price = #{price}

where id = #{id}

update  cloud_host

	real_host_id = #{real_host_id},
	sys_image_name = #{sys_image_name}

where real_host_id  = #{real_host_id}

mapper.delete:

delete from cloud_host
where type = 4
and id in (
	select w_detail.host_id
	from cloud_host_warehouse_detail w_detail
	
		and w_detail.warehouse_id in
		
			#{item}
		
		and w_detail.status != #{warehouseDetailStatus}
	
)
