mapper.select:
		select count(1)
		from suggestion s 
		left join (select id, case  when type=1 then x.account 
when type=2 then x.operatorname 
when type=3 then x.agentname
when type=4 then x.username end name,type,account from (
select a.id,a.type,a.account,b.name agentname,c.name operatorname,d.name username from sys_user a left join agent b on a.id = b.id
left join operator c on a.id = c.id
left join terminal_user d on a.id = d.id) x) u
			on s.user_id = u.id
		
			
				s.user_id = #{userId}
			
			
				and s.type = #{type}
			
			
				and s.status = #{status}
			
			
				and u.name like #{username}
			
			
				and date(s.submit_time) >= #{create_time_from}							
				
			
				and date(s.submit_time) <= #{create_time_to}

select s.id, s.content, u.account, s.type, s.submit_time, s.status, s.result, u.name username from suggestion s left join (select id, case when type=1 then x.account when type=2 then x.operatorname when type=3 then x.agentname when type=4 then x.username end name,type,account from ( select a.id,a.type,a.account,b.name agentname,c.name operatorname,d.name username from sys_user a left join agent b on a.id = b.id left join operator c on a.id = c.id left join terminal_user d on a.id = d.id) x) u on s.user_id = u.id
s.user_id = #{userId}
and s.type = #{type}
and s.status = #{status}
and u.name like #{username}
and date(s.submit_time) >= #{create_time_from}
and date(s.submit_time) <= #{create_time_to}
order by s.submit_time desc limit #{start_row}, #{row_count}

select s.id, s.content, u.account, s.type, s.submit_time, s.status, s.result, s.reply_time from suggestion s left join sys_user u on s.user_id = u.id
s.user_id = #{userId}
order by s.submit_time asc
