mapper.select:
select count(1)
from terminal_user t
where t.belonging_id = #{userId}

select count(1)
from terminal_user t
where t.belonging_id = #{userId}
	and t.create_time like #{currentMonth}

select count(1)
from terminal_user t
where t.belonging_id = #{userId}
	and t.create_time like #{time}

select count(1)
from terminal_user t
left join sys_user u
	on t.id = u.id
left join sys_user bu
	on t.belonging_id = bu.id
left join mark_bind_user mbu
	on t.id = mbu.user_id
left join mark m
	on mbu.mark_id = m.id
left join	
(select user_id ,sum(recharge) recharge,sum(consum) consum from (
select user_id,
sum(case when type = 1 then amount else 0 end) recharge,
sum(case when type = 2 then amount else 0 end) consum
from account_balance_detail where recharge_status = 2 group by user_id,type  ) x group by user_id) y on y.user_id = t.id				

	t.status != 5
	
		and t.belonging_id = #{belongingId}
	
	
		and u.account like #{terminalUserAccount}
	
	
		and t.phone like #{phone}
	
	
		and m.id = #{markId}
	
	
		
			and t.percent_off = #{percent_off}
		
		
			and t.percent_off > #{percent_off}
			
		
			and t.percent_off < #{percent_off}
										
	
	
		
			and t.account_balance = #{account_balance}
		
		
			and t.account_balance > #{account_balance}
			
		
			and t.account_balance < #{account_balance}
										
		
	
		
			and ifnull(y.recharge,0) = #{recharge}
		
		
			and ifnull(y.recharge,0) > #{recharge}
			
		
			and ifnull(y.recharge,0) < #{recharge}
										
		
	
		
			and ifnull(y.consum,0) = #{consum}
		
		
			and ifnull(y.consum,0) > #{consum}
			
		
			and ifnull(y.consum,0) < #{consum}
										
		
	
		and date(t.create_time) >= #{create_time_from}							
		
	
		and date(t.create_time) <= #{create_time_to}							
		
	
	and exists (
	select 1 from (select user_id,change_time from account_balance_detail
	where type = 1 and recharge_status = 2 and date(change_time) >=
	#{recharge_time_from}
	group by user_id,change_time
	) r1 where r1.user_id = t.id)
		
	
	and exists (
	select 1 from (select user_id,change_time from account_balance_detail
	where type = 1 and recharge_status = 2 and date(change_time) <=
	#{recharge_time_to}
	group by user_id,change_time
	) r2 where r2.user_id = t.id)
		
	
	and exists (
	select 1 from (select user_id,change_time from account_balance_detail
	where type = 2 and date(change_time) >=
	#{consum_time_from}
	group by user_id,change_time
	) c1 where c1.user_id = t.id)
		
	
	and exists (
	select 1 from (select user_id,change_time from account_balance_detail
	where type = 2 and date(change_time) <=
	#{consum_time_to}
	group by user_id,change_time
	)c2 where c2.user_id = t.id)

select 
	t.id, 
	u.account,
	u.group_id,
	t.name, 
	t.id_card, 
	t.email, 
	t.phone, 
	t.create_time,
	t.account_balance,
	t.status,
	t.percent_off,
	bu.account belonging_account,
	m.name markName,
	ifnull(y.recharge,0) recharge,
	ifnull(y.consum,0) consum,
	t.account_balance
from terminal_user t
left join sys_user u
	on t.id = u.id
left join sys_user bu
	on t.belonging_id = bu.id
left join mark_bind_user mbu
	on t.id = mbu.user_id
left join mark m
	on mbu.mark_id = m.id
left join	
(select user_id ,sum(recharge) recharge,sum(consum) consum from (
select user_id,
sum(case when type = 1 then amount else 0 end) recharge,
sum(case when type = 2 then amount else 0 end) consum
from account_balance_detail where recharge_status = 2 group by user_id,type  ) x group by user_id) y on y.user_id = t.id			

	t.status != 5
	
		and t.belonging_id = #{belongingId}
	
	
		and u.account like #{terminalUserAccount}
	
	
		and t.phone like #{phone}
	
	
		and m.id = #{markId}
	
	
		
			and t.percent_off = #{percent_off}
		
		
			and t.percent_off > #{percent_off}
			
		
			and t.percent_off < #{percent_off}
										
	
	
		
			and t.account_balance = #{account_balance}
		
		
			and t.account_balance > #{account_balance}
			
		
			and t.account_balance < #{account_balance}
										
		
	
		
			and ifnull(y.recharge,0) = #{recharge}
		
		
			and ifnull(y.recharge,0) > #{recharge}
			
		
			and ifnull(y.recharge,0) < #{recharge}
										
		
	
		
			and ifnull(y.consum,0) = #{consum}
		
		
			and ifnull(y.consum,0) > #{consum}
			
		
			and ifnull(y.consum,0) < #{consum}
										
		
	
		and date(t.create_time) >= #{create_time_from}							
		
	
		and date(t.create_time) <= #{create_time_to}							
		
	
	and exists (
	select 1 from (select user_id,change_time from account_balance_detail
	where type = 1 and recharge_status = 2 and date(change_time) >=
	#{recharge_time_from}
	group by user_id,change_time
	) r1 where r1.user_id = t.id)
		
	
	and exists (
	select 1 from (select user_id,change_time from account_balance_detail
	where type = 1 and recharge_status = 2 and date(change_time) <=
	#{recharge_time_to}
	group by user_id,change_time
	) r2 where r2.user_id = t.id)
		
	
	and exists (
	select 1 from (select user_id,change_time from account_balance_detail
	where type = 2 and date(change_time) >=
	#{consum_time_from}
	group by user_id,change_time
	) c1 where c1.user_id = t.id)
		
	
	and exists (
	select 1 from (select user_id,change_time from account_balance_detail
	where type = 2 and date(change_time) <=
	#{consum_time_to}
	group by user_id,change_time
	)c2 where c2.user_id = t.id)
								

order by t.create_time desc
limit #{start_row}, #{row_count}

mapper.update:
update terminal_user set  
	account_balance = #{accountBalance},
	balance_update_time    = #{balanceUpdateTime}
where id = #{id}

update terminal_user set name = #{name}, id_card = #{idCard}, email = #{email}, email_verified = #{emailVerified}, phone = #{phone}, phone_verified = #{phoneVerified}, status = #{status}, belonging_type = #{belongingType},
create_time = #{createTime},
percent_off =#{percentOff}
where id = #{id}

update terminal_user set 
	         email = #{email},
	email_verified = #{emailVerified}
where id = #{id}

update terminal_user set 
			 phone = #{phone},
	phone_verified = #{phoneVerified}
where id = #{id}

update terminal_user set 
	email_verified = #{emailVerified},
	status         = #{status}
where id = #{id}
