mapper.select:
select id, real_vpc_id, name, type, display_name, description, status, user_id, region, monthly_price, host_amount, ip_amount, create_time, modified_time from vpc_base_info
status != 3 and user_id = #{userId}
and region = #{region}
order by create_time desc
limit #{start_row}, #{row_count}

select count(1)
from vpc_base_info vbi
left join sys_user u
	on vbi.user_id = u.id
left join mark_bind_user mbu
	on u.id = mbu.user_id
left join mark m
	on mbu.mark_id = m.id

	vbi.status != 3
	
		and vbi.display_name like #{vpcName}
	
	
		and vbi.status = #{queryStatus}
	
	
		and vbi.region = #{region}
	
	
		and m.id = #{markId}

select vbi.id, vbi.real_vpc_id, vbi.name, vbi.type, vbi.display_name, vbi.description, vbi.status, vbi.user_id, vbi.region, vbi.monthly_price, vbi.host_amount, vbi.ip_amount, vbi.create_time, vbi.modified_time, u.account userName, u.id userId from vpc_base_info vbi left join sys_user u on vbi.user_id = u.id left join mark_bind_user mbu on u.id = mbu.user_id left join mark m on mbu.mark_id = m.id
vbi.status != 3
and vbi.display_name like #{vpcName}
and vbi.status = #{queryStatus}
and vbi.region = #{region}
and m.id = #{markId}
order by vbi.display_name limit #{start_row}, #{row_count}

mapper.update:
update vpc_base_info
set 
    ip_amount = ip_amount + #{amount},
	modified_time = #{modifyTime}
where  id = #{id}

update vpc_base_info
set 
    host_amount = host_amount + #{amount},
	modified_time = #{modifyTime}
where  id = #{id}

 
